mapper.select:
SELECT 

FROM zmkj_product_config a

WHERE a.id = #{id}

SELECT
FROM zmkj_product_config a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.technology_platform LIKE '%'||#{technologyPlatform}||'%' '%'+#{technologyPlatform}+'%' concat('%',#{technologyPlatform},'%')
AND a.version LIKE '%'||#{version}||'%' '%'+#{version}+'%' concat('%',#{version},'%')
AND a.version_num = #{versionNum}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM zmkj_product_config a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM zmkj_product_config a
WHERE a.name = #{name} and technology_platform = #{technologyPlatform} and version_num > #{versionNum}

mapper.update:
UPDATE zmkj_product_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
